mapper.select:
SELECT a.* ,IF(tk.associated_field is null ,false,true) as isAutoHandle FROM ( SELECT DISTINCT ti.id AS eventId, ti.category AS category, ti.sub_category AS subCategory, CASE alarm_status WHEN "unprocessed" THEN "未处置" WHEN "processing" THEN "处置中" WHEN "falsePositives" THEN "已处置-标记误报" WHEN "processed" THEN "已处置-完成" WHEN "ignore" THEN "已处置-忽略" END alarmStatus, ti.focus_ip AS focusIp, DATE_FORMAT( end_time, '%Y-%m-%d %T' ) AS endTime, DATE_FORMAT( start_time, '%Y-%m-%d %T' ) AS startTime, DATE_FORMAT( end_time, '%Y-%m-%d' ) AS timePart, counts AS eventCount, CASE alarm_results WHEN "OK" THEN "成功" WHEN "FAIL" THEN "失败" WHEN "UNKNOWN" THEN "尝试" END alarmResults, CASE threat_severity WHEN "Low" THEN "低" WHEN "Medium" THEN "中" WHEN "High" THEN "高" ELSE "未知" END threatSeverity, event_name AS alarmName, tt.agg_field AS aggField, ti.attacker, ti.victim, ti.focus, ti.tag_search AS tagSearch, IF ( td.scenario_id IS NULL, FALSE, TRUE ) AS isScenario , tm.incident_type as incidentType FROM t_security_incidents ti LEFT JOIN t_query_template tt ON ti.template_code = tt.template_code LEFT JOIN t_event_scenario_data td ON ti.id = td.incident_id LEFT JOIN t_event_template tm ON ti.template_id = tm.id WHERE ti.id IN
#{item,jdbcType=BIGINT}
) a LEFT JOIN t_alarm_status_timing_task tk ON a.eventId = tk.associated_field ORDER BY endTime DESC

SELECT ti.threat_severity as threatSeverity, count( 1 ) AS total FROM t_security_incidents ti WHERE ti.start_time BETWEEN #{startTime} AND #{endTime}
AND ti.event_name like concat('%',#{keyword},'%') OR ti.attacker like concat('%',#{keyword},'%') OR ti.victim like concat('%',#{keyword},'%')
GROUP BY ti.threat_severity

SELECT count(1) as number,a.eventName FROM ( SELECT
ti.sub_category as eventName
ti.event_name as eventName
FROM t_security_incidents ti LEFT JOIN t_event_scenario_data td ON ti.id = td.incident_id LEFT JOIN t_event_template tm ON ti.template_id = tm.id WHERE ti.end_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
AND ti.focus is not null
AND ti.focus is null
${scenarioType}
AND ti.event_name LIKE concat('%',#{eventName},'%')
AND ti.focus_ip LIKE concat('%',#{focusIp},'%')
AND ti.sub_category IN
#{sub}
AND ti.alarm_status IN
#{status}
AND ti.alarm_results IN
#{result}
AND ti.threat_severity IN
#{severity}
AND
ti.tag_search like BINARY concat('%',#{tag},'%')
AND
ti.kill_chain LIKE concat('%',#{killC},'%')
) as a GROUP BY a.eventName ORDER BY number

SELECT a.* ,IF(tk.associated_field is null ,false,true) as isAutoHandle FROM ( SELECT DISTINCT ti.id AS eventId, ti.category AS category, ti.sub_category AS subCategory, CASE alarm_status WHEN "unprocessed" THEN "未处置" WHEN "processing" THEN "处置中" WHEN "falsePositives" THEN "已处置-标记误报" WHEN "processed" THEN "已处置-完成" WHEN "ignore" THEN "已处置-忽略" END alarmStatus, ti.focus_ip AS focusIp, DATE_FORMAT( end_time, '%Y-%m-%d %T' ) AS endTime, DATE_FORMAT( start_time, '%Y-%m-%d %T' ) AS startTime, DATE_FORMAT( end_time, '%Y-%m-%d' ) AS timePart, counts AS eventCount, CASE alarm_results WHEN "OK" THEN "成功" WHEN "FAIL" THEN "失败" WHEN "UNKNOWN" THEN "尝试" END alarmResults, CASE threat_severity WHEN "Low" THEN "低" WHEN "Medium" THEN "中" WHEN "High" THEN "高" ELSE "未知" END threatSeverity, event_name AS alarmName, tt.agg_field AS aggField, ti.attacker, ti.victim, ti.focus, ti.tag_search AS tagSearch, IF ( td.scenario_id IS NULL, FALSE, TRUE ) AS isScenario , tm.incident_type as incidentType FROM t_security_incidents ti LEFT JOIN t_query_template tt ON ti.template_code = tt.template_code LEFT JOIN t_event_scenario_data td ON ti.id = td.incident_id LEFT JOIN t_event_template tm ON ti.template_id = tm.id WHERE ti.end_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
AND ti.event_name LIKE concat('%\\',#{eventName},'%')
AND ti.focus_ip LIKE concat('%',#{focusIp},'%')
AND ti.sub_category IN
#{sub}
AND ti.alarm_status IN
#{status}
AND ti.alarm_results IN
#{result}
AND ti.threat_severity IN
#{severity}
AND
ti.tag_search like BINARY concat('%',#{tag},'%')
AND
ti.kill_chain LIKE concat('%',#{killC},'%')
) a LEFT JOIN t_alarm_status_timing_task tk ON a.eventId = tk.associated_field and task_type = 'SecurityEvent'
WHERE isScenario = #{isScenario,jdbcType=BOOLEAN}
ORDER BY
endTime desc
${orderByStr}
limit #{offset}, #{size}

