SELECT DISTINCT ti.id AS eventId, ti.category as category, ti.sub_category AS subCategory, alarm_status AS alarmStatus, ti.focus_ip as focusIp, DATE_FORMAT(end_time,'%Y-%m-%d %T') as endTime, DATE_FORMAT(start_time,'%Y-%m-%d %T') as startTime, counts as eventCount, alarm_results AS alarmResults, threat_severity AS threatSeverity, event_name as alarmName, tt.agg_field as aggField,ti.attacker,ti.victim,ti.focus, ti.tag_search as tagSearch, IF(td.scenario_id is null ,false,true) as isScenario FROM t_security_incidents ti LEFT JOIN t_query_template tt ON ti.template_code = tt.template_code LEFT JOIN t_event_scenario_data td ON ti.id = td.incident_id WHERE ti.end_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
AND ti.event_name LIKE concat('%',#{eventName},'%')
