SELECT COUNT(1) FROM ( SELECT DISTINCT ti.id AS eventId, ti.category AS category, ti.sub_category AS subCategory, CASE alarm_status WHEN "unprocessed" THEN "未处置" WHEN "processing" THEN "处置中" WHEN "falsePositives" THEN "已处置-标记误报" WHEN "processed" THEN "已处置-完成" WHEN "ignore" THEN "已处置-忽略" END alarmStatus, ti.focus_ip AS focusIp, DATE_FORMAT( end_time, '%Y-%m-%d %T' ) AS endTime, DATE_FORMAT( start_time, '%Y-%m-%d %T' ) AS startTime, DATE_FORMAT( end_time, '%Y-%m-%d' ) AS timePart, counts AS eventCount, CASE alarm_results WHEN "OK" THEN "成功" WHEN "FAIL" THEN "失败" WHEN "UNKNOWN" THEN "尝试" END alarmResults, CASE threat_severity WHEN "Low" THEN "低" WHEN "Medium" THEN "中" WHEN "High" THEN "高" ELSE "未知" END threatSeverity, event_name AS alarmName, tt.agg_field AS aggField, ti.attacker, ti.victim, ti.focus, ti.tag_search AS tagSearch, IF ( td.scenario_id IS NULL, FALSE, TRUE ) AS isScenario , tm.incident_type as incidentType FROM t_security_incidents ti LEFT JOIN t_query_template tt ON ti.template_code = tt.template_code LEFT JOIN t_event_scenario_data td ON ti.id = td.incident_id LEFT JOIN t_event_template tm ON ti.template_id = tm.id WHERE ti.end_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
AND ti.event_name LIKE concat('%',#{eventName},'%')
