select count(1) as eventCount,a.threatSeverity from ( SELECT threat_severity AS threatSeverity FROM t_security_incidents ti LEFT JOIN t_event_scenario_data td ON ti.id = td.incident_id LEFT JOIN t_event_template tm ON ti.template_id = tm.id WHERE ti.end_time BETWEEN #{startTime,jdbcType=VARCHAR} AND #{endTime,jdbcType=VARCHAR}
AND ti.event_name LIKE concat('%',#{eventName},'%')
${scenarioType}
