AND ti.sub_category IN
#{sub}
AND ti.alarm_status IN
#{status}
AND ti.alarm_results IN
#{result}
AND ti.threat_severity IN
#{severity}
AND
ti.tag_search like BINARY concat('%',#{tag},'%')
