mapper.select:
select t.event_name as "name", t.category as topEventTypeChinese, t.sub_category as secondEventTypeChinese, t.id as securityIncidentId, t.start_time as startTime, t.end_time as endTime, tet.incident_sub_class_type as ruleType, t.focus_ip as focusIp, t.attacker as attacker, t.victim as victim, t.threat_severity as severity, t.alarm_results as catOutCome, t.kill_chain as killChain, t.is_scenario as isScenario from t_security_incidents t,t_event_template tet where tet.incident_type = true and t.template_id = tet.id
and t.id IN
#{item}

update.foreach:
update t_risk_incidents_out_going
set payload = #{item.payload},more_field = #{item.moreField}
where id = #{item.id}

mapper.delete:
delete from t_risk_incidents_history
where create_time <= #{timestamp};
delete from t_risk_incidents_out_going_history
where time_part <= #{timestamp};

mapper.select:
select focus_ip as focusIp, count(*) as riskCount from t_risk_incidents_out_going
and focus_ip IN
#{item}
and end_time BETWEEN CAST(#{startTime} AS timestamp) AND CAST(#{endTime} AS timestamp)
group by focus_ip order by riskCount desc limit #{top}

mapper.delete:
DELETE FROM t_risk_incidents_out_going_history 
WHERE id IN

    #{id}
